mapper.select:
SELECT test_list_id, tmp2.test_code, test_name, tmp2.test_details_id, test_result_value, test_details_code, test_details_name, test_details_unit, test_details_min, test_details_max FROM (SELECT tmp.test_list_id, tmp.test_code, test_list_req, test_details_id, test_result_value FROM (select * from tests_list where test_list_id = #{test_list_id} AND test_list_req = 1
AND reception_id = #{reception_id}
) AS tmp, test_results WHERE tmp.test_list_id = test_results.test_list_id AND tmp.test_code = test_results.test_code) AS tmp2, test_details WHERE tmp2.test_details_id = test_details.test_details_id

mapper.update:
update tests_list
set test_list_state = #{state}
where test_list_id = #{test_list_id} and test_list_req = 1 and reception_id=#{reception_id}

update test_results
   set test_result_value = #{test_result_value}
   where test_list_id = #{test_list_id} And test_code = #{test_code} And test_details_id = #{test_details_id}

mapper.select:
select test_list_state
from tests_list
where test_list_id = #{test_list_id}

mapper.update:
update reception
set reception_state = #{state}
where reception_id = #{reception_id}

mapper.select:
select test_list_saved
from tests_list
where test_list_id = #{test_list_id}
