mapper.select:
select patient_id ,patient_name ,patient_tel ,FLOOR((CAST(REPLACE(CURRENT_DATE,'-','') AS UNSIGNED) - CAST(REPLACE(date_format(patient_birth, '%Y-%m-%d'),'-','') AS UNSIGNED)) / 10000 ) AS patient_birth ,patient_medicine ,patient_disease ,patient_comment ,patient_gender ,patient_recent_visit from patient where
patient_id= #{item}
order by field(patient_id,
#{item}
)

select treatment_record
from treatment
where treatment_id = #{treatment_id}

select treatment_comment
from treatment
where treatment_id = #{treatment_id}

select reception_id
from treatment
where treatment_id = #{treatment_id}

select reception_content
from reception
where reception_id = #{reception_id}

mapper.update:
update treatment
set treatment_record = #{treatment_record}
   ,treatment_comment = #{treatment_comment}
   ,treatment_saved = 1
where treatment_id = #{treatment_id}

update reception
set reception_state = "완료"
where reception_id = #{reception_id}

mapper.select:
select appointment.appointment_id
from appointment, reception
where appointment.appointment_id = reception.appointment_id and reception_id = #{reception_id}

mapper.update:
update appointment
set appointment_state = "완료"
where appointment_id = #{appointment_id}

mapper.select:
select test_details_id
from test_details
where test_code = #{test_code}

select patient_id
from treatment
where treatment_id = #{treatment_id}

select staff_id
from treatment
where treatment_id = #{treatment_id}

select distinct(test_list_id) 
from tests_list
where treatment_id = #{treatment_id}

mapper.update:
update tests_list
set reception_id = #{latelyReceptionId}
where test_list_id = #{test_list_id}

mapper.select:
select test_details.test_code ,test_name ,test_details_code ,test_details_name ,test_details_unit ,test_details_min ,test_details_max ,test_result_value from test_details, test_results where test_results.test_details_id = test_details.test_details_id and (
test_list_id = #{item}
)
